custom_scss:
.for-login .page-card, .for-forgot .page-card, .for-login-with-email-link .page-card, .for-signup .page-card, .for-email-login .page-card {
    background-color: rgb(63, 122, 200) !important;
    max-width: 400px;
    padding: 45px 10px;
    margin: 0px auto;
    border-radius: var(--border-radius-md);
    border: 1px solid var(--border-color);
}

.navbar-light {
    border-bottom: 1px solid rgb(226, 226, 226);
    background: rgb(236, 131, 5);
}

body{
    background-color: rgb(9, 16, 87) !important;
}

.page-card-head h4 {
    margin-top: 1rem;
    font-size: var(--text-xl);
    font-weight: var(--weight-semibold);
    letter-spacing: 0.01em;
    color: #fffcfc !important;
}

.for-login .page-card .page-card-body .forgot-password-message > *, .for-forgot .page-card .page-card-body .forgot-password-message > *, .for-login-with-email-link .page-card .page-card-body .forgot-password-message > *, .for-signup .page-card .page-card-body .forgot-password-message > *, .for-email-login .page-card .page-card-body .forgot-password-message > * {
    color: #ffffff !important;
    font-size: var(--text-sm);
    font-weight: var(--weight-regular);
    letter-spacing: 0.02em;
}

.navbar-light .navbar-brand {
    color: rgb(255 255 255) !important;
}

.text-muted {
    color: #ffffff !important;
}

.btn.btn-primary {
    background-color: #0c0c4f;
    color: #fdfdfd;
    white-space: nowrap;
    --icon-stroke: rgb(255 254 254);
    --icon-fill-bg: #000000;
}

.for-login .page-card .page-card-body .password-field .toggle-password {
    color: black !important;
}

.sidebar-item a{
    color: white !important;
}

.my-account-header {
    color: white !important;
}

.standard-sidebar-item.selected .sidebar-item-label.selected {
    color: black !important;
}

